function sumValues(items, propName) {
    return items.reduce((total, item) => total + (parseFloat(item[propName] || 0)), 0);
}

function calculateInvoiceTotals(invoice) {
    const lineItems = invoice.line_items || [];
    const subtotal = sumValues(lineItems, 'line_amount').toFixed(2);
    const totalTax = sumValues(lineItems, 'tax_amount').toFixed(2);
    const total = (parseFloat(subtotal) + parseFloat(totalTax)).toFixed(2);

    let changes = {};
    if (invoice.subtotal !== subtotal) changes.subtotal = subtotal;
    if (invoice.total_tax !== totalTax) changes.total_tax = totalTax;
    if (invoice.total !== total) changes.total = total;

    return {
        changes: Object.keys(changes).length > 0 ? changes : null,
        total: total
    };
}

function calculateInvoicePayments(invoice, calculatedTotal) {
    const payments = invoice.payments || [];
    const totalPayments = sumValues(payments, 'amount').toFixed(2);
    const amountDue = (parseFloat(calculatedTotal) - parseFloat(totalPayments)).toFixed(2);

    let changes = {};
    if (invoice.amount_due !== amountDue) changes.amount_due = amountDue;
    if (invoice.amount_paid !== totalPayments) changes.amount_paid = totalPayments;

    if (invoice.status === 'paid' && parseFloat(totalPayments) < parseFloat(calculatedTotal)) {
        changes.status = 'unpaid';
    } else if (invoice.status !== 'paid' && parseFloat(totalPayments) >= parseFloat(calculatedTotal)) {
        changes.status = 'paid';
    }

    return Object.keys(changes).length > 0 ? changes : null;
}

module.exports = async function processInvoiceChanges(data) {
    const invoice = data.invoice;
    if (!invoice) throw new Error("Invalid invoice data");

    const { changes: totalsChanges, total: calculatedTotal } = calculateInvoiceTotals(invoice);
    const paymentChanges = calculateInvoicePayments(invoice, calculatedTotal);

    if (!totalsChanges && !paymentChanges) throw new Error('No changes necessary');

    return { ...totalsChanges, ...paymentChanges };
};

module.exports = async function (data) {
	// Index data to get the string you want to slugify
	// Assign it to the "text" var below.
	const text = data.$trigger.payload.title;

	const slug = text
		.toLowerCase()
		.trim()
		.replace(/[^\w\s-]/g, '')
		.replace(/[\s_-]+/g, '-')
		.replace(/^-+|-+$/g, '');

	return slug;
};

