function extractDomain(inputUrl) {
	// Regular expression to extract domain from URL
	const regex = /^(?:https?:\/\/)?(?:www\.)?([^\/?#]+)(?:[\/?#]|$)/i;
	const matches = inputUrl.match(regex);

	// Return the matched domain or undefined if not found
	return matches && matches[1];
}

module.exports = function (data) {
	const website = data.$trigger.payload.website;

	const domain = extractDomain(website);

	if (!domain) {
		throw new Error('Unable to properly determine the domain for this website');
	}
	return domain;
};

module.exports = async function (data) {
	const item = { ...data.invoice_items, ...data.$trigger.payload };

	// Basic validation for unit_price and quantity
	if (!item.unit_price || !item.quantity) {
		throw new Error('Missing or invalid unit_price or quantity.');
	}

	// Calculate line_amount
	const lineAmount = parseFloat(item.unit_price) * parseFloat(item.quantity);
	if (isNaN(lineAmount)) {
		throw new Error('Error calculating line_amount.');
	}
	item.line_amount = lineAmount;

	// Validate and calculate tax_amount if tax_rate is provided
	if (item.tax_rate && item.tax_rate.rate) {
		const taxRateId = item.tax_rate.id;
		const taxAmount = (lineAmount * parseFloat(item.tax_rate.rate)) / 100;
		if (isNaN(taxAmount)) {
			throw new Error('Error calculating tax_amount.');
		}
		item.tax_amount = taxAmount;

		delete item.tax_rate;
		item.tax_rate = taxRateId;
	}

	return item;
};

module.exports = async function(data) {
    // Validate necessary fields in the trigger data
    if (!data || !data.$trigger || !data.$trigger.body || !data.get_project_template || !data.get_project_template.tasks) {
        throw new Error('Invalid or missing data in trigger payload');
    }

    const { project_start_date, project_due_date } = data.$trigger.body;
    const projectStartDate = project_start_date ? new Date(project_start_date) : new Date();
    const projectDueDate = project_due_date ? new Date(project_due_date) : new Date();

    const templateTasks = data.get_project_template.tasks;
    const projectTasks = [];

    for (const task of templateTasks) {
        let taskStartDate;
        let taskDueDate;

        if (task.set_dates_relative_to === 'project_start_date' && project_start_date) {
            taskStartDate = new Date(projectStartDate);
            taskStartDate.setDate(taskStartDate.getDate() + (task.start_days || 0));

            taskDueDate = new Date(projectStartDate);
            taskDueDate.setDate(taskDueDate.getDate() + (task.due_days || 0));
        } else if (task.set_dates_relative_to === 'project_due_date' && project_due_date) {
            taskStartDate = new Date(projectDueDate);
            taskStartDate.setDate(taskStartDate.getDate() - (task.start_days || 0));

            taskDueDate = new Date(projectDueDate);
            taskDueDate.setDate(taskDueDate.getDate() - (task.due_days || 0));
        }

        // If dates are not calculated due to missing project dates, default to null
        const newTask = {
            name: task.name,
            description: task.description,
            type: task.type,
            start_date: taskStartDate ? taskStartDate.toISOString() : null,
            due_date: taskDueDate ? taskDueDate.toISOString() : null,
            is_visible_to_client: task.is_visible_to_client ?? false,
            assigned_to: task.default_team_assigned_to?.key ?? null,
            form: task.form ?? null,
            embed_url: task.embed_url ?? null
        };

        projectTasks.push(newTask);
    }

    const project = {
        name: data.$trigger.body.name,
        tasks: projectTasks,
        organization: data.$trigger.body.organization.key,
        contacts: [
            { contacts_id: data.$trigger.body.contact.key }
        ],
        owner: data.$accountability.user,
        start_date: project_start_date ? projectStartDate.toISOString() : null,
        due_date: project_due_date ? projectDueDate.toISOString() : null,
    };

    return project;
};

